import React from 'react'

import { useDispatch } from 'react-redux'
import { Card, Icon, Image } from 'semantic-ui-react'
import { useHistory } from "react-router-dom";

import { setBeer } from '../app/beersSlice'

import '../App.css';

function CardBeers({ beer }) {
    const dispatch = useDispatch()
    const history = useHistory();

    const handleGoback = () => {
        dispatch(setBeer({}))
        history.push(`/`)
    }

    return (
        <Card style={{
            marginLeft: "20px",
            marginRight: "20px",
            height: "450px",
        }} key={parseInt(beer.id)}>
            <div style={{
                marginTop: 10,
                marginBottom: 10,
                width: "300px"
            }}>
                <Image size="mini" src={beer.image_url} wrapped />
            </div>
            <Card.Content>
                <Card.Header style={{ fontSize: 20 }}>{beer.name}</Card.Header>
                <Card.Meta>
                    <span style={{ fontSize: 19 }} className='date'>First brewed in {beer.first_brewed}</span>
                </Card.Meta>
                <Card.Description>
                    <div style={{
                        display: 'flex',
                        flexDirection: 'row',
                        alignItems: 'center'
                    }}>
                        <Icon name="hand point right outline" size="large" color="black" />
                        <p style={{ fontSize: 17 }}>{beer.brewers_tips}
                        </p>
                    </div>
                </Card.Description>
            </Card.Content>
            <Card.Content extra>
                <a style={{ fontSize: 20 }} onClick={handleGoback}>
                    <Icon name='angle left' />
                    Back
                </a>
            </Card.Content>
        </Card>
    )
}

export default CardBeers;
