import React, { useEffect } from 'react'

import { useSelector, useDispatch } from 'react-redux'
import axios from 'axios'

import { setBeers } from '../app/beersSlice'

import { Icon } from 'semantic-ui-react'

import Loader from '../component/Loader'
import RenderBeers from '../component/RenderBeers'

import '../App.css';

function Beers() {
  const beers = useSelector(state => state.beers.beers)
  const dispatch = useDispatch()

  useEffect(() => {
    setTimeout(() => {
      axios.get('https://api.punkapi.com/v2/beers').then(({ data }) => dispatch(setBeers(data)))
    }, 2000)
  }, [dispatch]);

  return beers.length === 0 ?
    <div className="App">
      <header className="App-header">
        <Loader text={"Beers is coming"} icon={<Icon name="beer" size="small" />} />
      </header>
    </div> : (
      <div className="App">
        <header className="App-header2">
          <RenderBeers beers={beers} />
        </header>
      </div>
    );
}

export default Beers;
